CREATE TABLE IF NOT EXISTS `cartrolandofc`.`rodada_pontuacao` ( `id` INT NOT NULL AUTO_INCREMENT , `rodada` INT NOT NULL , `atleta_id` INT NOT NULL , `apelido` VARCHAR(200) NOT NULL, `pontuacao` DECIMAL (5,2) NOT NULL, `foto` VARCHAR(255), `posicao_id` INT NOT NULL , `clube_id` INT NOT NULL, `criado` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`), INDEX (`rodada`), INDEX  ( `atleta_id`)) ENGINE = InnoDB;